    // Executing JS Code on a JSON Object
    public static void main(String... arg) throws ScriptException, NoSuchMethodException, IOException {

//        System.setProperty("polyglot.js.nashorn-compat", "true");
        
        String jsScript =
                "let originalData = " + SOME_JSON + ";\n" +
                "print('From JS - Orig Data: ' + JSON.stringify(originalData, null, 2));\n" +
                "let extracted = originalData.problems; // JSON.parse(data).problems;\n" +
                "print('From JS - Extracted:' + JSON.stringify(extracted));\n" +
                "// Compose message array\n" +
                "let messArray = [];\n" +
                "extracted.forEach(mess => {\n" +
                "    messArray.push(mess.severity + ' - ' + mess.message);\n" +
                "});\n" +
                "let finalResult = JSON.stringify(messArray);\n" +
                "print('Done with JS');";

        ScriptEngine graalEngine = new ScriptEngineManager().getEngineByName("graal.js");

        // Start here.
        System.out.println(jsScript);
        graalEngine.eval(jsScript);

        // Get the variable by its name.
        Object result = graalEngine.eval("finalResult"); // ""messArray");

        System.out.println("\nFrom Java:" + result);
        System.out.println("Got a " + result.getClass().getName());

        ObjectMapper mapper = new ObjectMapper();
        Object obj = mapper.readValue(result.toString(), Object.class);
        System.out.println("Returned a " + obj.getClass().getName());
        if (obj instanceof List<?>) {
            List<?> list = (List<?>) obj;
            list.forEach(elem -> {
                System.out.printf("-- (%s) %s\n", elem.getClass().getName(), elem);
            });
        }

        System.out.println("Done!");
    }
